<script type="text/javascript">
	var interval = setInterval(checkElement, 1000);
	var container = document.getElementById('main-menu-container-internal');
	var closeButtonLeft = document.getElementById('main-menu__close-btn-left');
	var containers = [];

	function checkElement(){
		var container = document.getElementsByClassName('header-row__burger-trigger');
		if(container.length > 0){
			clearInterval(interval);
			container[0].addEventListener('click', clickMenu);

			var containers = [ 
				document.getElementsByClassName('hero__content')[0],
				document.getElementsByClassName('section-statistic')[0],
				document.getElementsByClassName('section-guarantees')[0],
				document.getElementsByClassName('section section-rewards')[0],
				document.getElementsByClassName('section-club')[0],
				document.getElementsByClassName('section-support')[0],
				document.getElementsByClassName('section-closing section-closing_about')[0],
				document.getElementById('footer'),
				document.getElementsByClassName('submenu-module')[0],
				document.getElementsByClassName('header-row__nav')[0],
				document.getElementsByClassName('header-row__right')[0]
			];

			for(let i = 0; i < containers.length; i++){
				containers[i].addEventListener('click', closeMenu);
			}
			let a_list = document.getElementsByClassName('header-row__nav nav-top')[0].getElementsByTagName('a');
			a_list[a_list.length - 1].href = '/en/assets';
			console.log(a_list[a_list.length - 1].href);
		}
	}

	function clickMenu(){
		container.classList.add('main-menu-container_active');
	}    
	closeButtonLeft.addEventListener('click', closeMenu);

	function closeMenu(){
		container.classList.remove('main-menu-container_active');
	}
</script>
